import React, {useState} from "react";
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Zoom from '@material-ui/core/Zoom';

function CreateArea(props) {
    const [content, contentFunction] = useState({
        title: "",
        note: ""
    });

    function contentChange(event){
        var {name, value} = event.target;
        contentFunction((prevValue) => {
            return {
                ...prevValue,
                [name]: value
            };
        });
    }

    function addContent(event){
        props.onAdd(content);
        event.preventDefault();
        contentFunction({title: "", note: ""})
    }

  return (
    <div>
      <form className="create-note">
        <input name="title" placeholder="Title" value = {content.title} onChange = {contentChange}/>
        <textarea name="note" placeholder="Take a note..." rows="3" value = {content.note} onChange = {contentChange}/>
        <Fab onClick = {addContent}><AddIcon /></Fab>
      </form>
    </div>
  );
}

export default CreateArea;
